public class ADD_Multi_DIV {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=15;
		int sum=a+b;
		float div=a/b;
		int mult=a*b;
		
		System.out.println("sum of a and b is ="+sum);
		System.out.println("Div of a and is"+div);
		System.out.println("multi of a and b is"+mult);


	}

}
